//Verificação de vizinhos não visitados no nó atual
bool verify_neighbor(node *current_node, int maze_to_visit[maze_size_row][maze_size_col], display *maze_display){
    //printf("\n--VERIFY NEIGHBOR--");

    pos *visited_pos = (pos*) malloc(sizeof(pos));
    bool exist_neighbor = false;
    
    //Redefine referências de vizinhos
    neighbor_right = false;
    neighbor_left = false;
    neighbor_up = false;
    neighbor_down = false;

    visited_pos->row = current_node->visited_pos[0];
    visited_pos->col = current_node->visited_pos[1];

    if((visited_pos->row == end_pos[0] && visited_pos->col == end_pos[1]) ||
       (visited_pos->row == end_pos[2] && visited_pos->col == end_pos[3]) ||
       (visited_pos->row == end_pos[4] && visited_pos->col == end_pos[5]) ||
       (visited_pos->row == end_pos[6] && visited_pos->col == end_pos[7])){
        neighbor_right = false;
        neighbor_left = false;
        neighbor_up = false;
        neighbor_down = false;
        return exist_neighbor;
    }

    //Vizinho em cima
    if(visited_pos->row - 1 >= 0 && maze_to_visit[visited_pos->row-1][visited_pos->col] == 0){
        neighbor_up = true; //ainda não foi visitado
        exist_neighbor = true;
        //printf("\nExiste vizinho em cima!");
    }

    //Vizinho embaixo
    if(visited_pos->row + 1 < maze_size_row && maze_to_visit[visited_pos->row+1][visited_pos->col] == 0){
        neighbor_down = true; //ainda não foi visitado
        exist_neighbor = true;
        //printf("\nExiste vizinho embaixo!");
    }

    //Vizinho na esquerda
    if(visited_pos->col - 1 >= 0 && maze_to_visit[visited_pos->row][visited_pos->col-1] == 0){
        neighbor_left = true; //ainda não foi visitado
        exist_neighbor = true;
        //printf("\nExiste vizinho na esquerda!");
    }

    //Vizinho na direita
    if(visited_pos->col + 1 < maze_size_col && maze_to_visit[visited_pos->row][visited_pos->col+1] == 0){
        neighbor_right = true; //ainda não foi visitado
        exist_neighbor = true;
        //printf("\nExiste vizinho na direita!");
    }

    /*if(exist_neighbor == false)
        printf("\nSem vizinhos n%co visitados!", 198);*/

    return exist_neighbor;
}

bool maze_visited(int maze_to_visit[maze_size_row][maze_size_col]){
    //printf("\n\n--MAZE VISITED--");
    int row, col; 

    //Verificar se há nós não visitados
    for(row = 0; row < maze_size_row; row++){
        for(col = 0; col < maze_size_col; col++){
            if(maze_to_visit[row][col] == 0){
                return true;
            }
        }
    }
    
    //printf("\nTodos os n%cs foram visitados | Labirinto concluido!\n", 162); //Não há mais nós a serem visitados
    return false;
}

void maze_display_print(display *maze_display){
    int row, col;

    for(row = 0; row < maze_size_row+1; row++){
        printf("\n");
        for(col = 0; col < maze_size_col+maze_size_col+1; col++){
            printf("%c", maze_display->pos_maze[row][col]);
        }
    }
    printf("\n");
}

display *maze_display_begin(){
    int row, col;
    display *maze_display = (display*) malloc(sizeof(display));

    for(row = 0; row < maze_size_row+1; row++){
        for(col = 0; col < maze_size_col+maze_size_col+1; col++){
            
            if(row != 0 && (col == 0 || col%2 == 0))
                maze_display->pos_maze[row][col] = '|';

            else 
            maze_display->pos_maze[row][col] = '_';
        }
    }
    return maze_display;
}

display *remove_wall(pos *visited_pos, node *current_node, pos *neighbor_pos, display *maze_display){
    int maze_row, maze_col;

    if(neighbor_pos->col == visited_pos->col+1){ //vizinho da direita
        maze_row = visited_pos->row+1;
        maze_col = visited_pos->col + visited_pos->col + 1;    
        maze_display->pos_maze[maze_row][maze_col+1] = '_';
        //maze_display_print(maze_display);
    }

    else if(neighbor_pos->col == visited_pos->col-1){ //vizinho da esquerda
        maze_row = visited_pos->row+1;
        maze_col = visited_pos->col + visited_pos->col + 1;    
        maze_display->pos_maze[maze_row][maze_col-1] = '_';
        //maze_display_print(maze_display);
    }

    else if(neighbor_pos->row == visited_pos->row-1){ //vizinho de cima
        maze_row = visited_pos->row+1;
        maze_col = visited_pos->col + visited_pos->col + 1;    
        maze_display->pos_maze[maze_row-1][maze_col] = ' ';
        //maze_display_print(maze_display);
    }

    else if(neighbor_pos->row == visited_pos->row+1){ //vizinho de baixo
        maze_row = visited_pos->row+1;
        maze_col = visited_pos->col + visited_pos->col + 1;    
        maze_display->pos_maze[maze_row][maze_col] = ' ';
        //maze_display_print(maze_display);
    }

    return maze_display;
}

display *generate_maze(stk *stk_visited){
    //printf("\n\n--GENERATE MAZE--");
    pos *visited_pos = (pos*) malloc(sizeof(pos));
    pos *neighbor_pos = (pos*) malloc(sizeof(pos));
    display *maze_display = (display*) malloc(sizeof(display)); //labirinto com configurações para exibição
    node *current_node = NULL;
    int maze_to_visit[maze_size_row][maze_size_col]; //matriz com informações dos nós visitados
    bool node_to_visit = true; //verifica se há nós a serem visitados
    int count = 0; //passos do algoritmo
    int row, col;

    //Inicializações
    for(row = 0; row < maze_size_row; row++){
        for(col = 0; col < maze_size_col; col++){
            maze_to_visit[row][col] = 0; //0 (false/nó não visitado) em todas as posições
        }
    }

    //pos_begin: define a posição de início e fim do labirinto aleatoriamente
    visited_pos = pos_begin(visited_pos);
    
    //maze_display: labirinto mostrado do cmd
    maze_display = maze_display_begin(maze_display);
    maze_display->pos_maze[begin_pos[0]+1][begin_pos[1]+begin_pos[1]] = '>';
    
    //Criando quadrado 2x2 do ponto de chegada
    maze_display->pos_maze[end_pos[0]+1][end_pos[1]+end_pos[1]+1] = ' ';
    maze_display->pos_maze[end_pos[0]+1][end_pos[1]+end_pos[1]+2] = ' ';
    maze_display->pos_maze[end_pos[0]+2][end_pos[1]+end_pos[1]+2] = '_';
    maze_display->pos_maze[end_pos[2]+1][end_pos[3]+end_pos[1]+2] = ' ';

    //maze_display_print(maze_display);

    /*printf("\n...IN%cCIO DO ALGORITMO DEPTH FIRST SEARCH | GERA%c%cO DO LABIRINTO...", 214, 128, 199);
    maze_display_print(maze_display);*/

    //Primeiro nó:
    maze_to_visit[visited_pos->row][visited_pos->col] = 1; //primeiro nó marcado como visitado
    stk_visited = push(visited_pos, stk_visited); //primeiro nó inserido no stk_visited

    //Algoritmo Depth-First Search (DFS)
    while(node_to_visit){ //enquanto houver nó a ser visitado
        //maze_display_print(maze_display);
        count++;
        //printf("\n\n--ALGORITMO DEPTH-FIRST SEARCH (DFS) | PASSOS EXECUTADOS: %d--", count);
        
        current_node = pop(stk_visited); //nó retirado do stk_visited para manipulação
        //printf("\nN%c atual: [%d]x[%d]", 162, current_node->visited_pos[0], current_node->visited_pos[1]);
        
        visited_pos->row = current_node->visited_pos[0]; //linha
        visited_pos->col = current_node->visited_pos[1]; //coluna

        if(verify_neighbor(current_node, maze_to_visit, maze_display)){ //se houver vizinhos não visitados retorna true
            stk_visited = push(visited_pos, stk_visited); //devolve o current_node para a pilha
            neighbor_pos = choose_neighbor(current_node); //retornar linha e coluna do vizinho escolhido
            //printf("\nVizinho escolhido: [%d]x[%d]", neighbor_pos->row, neighbor_pos->col);
            //printf("\nPassos executados: %d", count);

            maze_display = remove_wall(visited_pos, current_node, neighbor_pos, maze_display); //remove parede

            //Marca posição como visitada na matriz de referência  
            if((neighbor_pos->row == end_pos[0] && neighbor_pos->col == end_pos[1]) ||
               (neighbor_pos->row == end_pos[2] && neighbor_pos->col == end_pos[3]) ||
               (neighbor_pos->row == end_pos[4] && neighbor_pos->col == end_pos[5]) ||
               (neighbor_pos->row == end_pos[6] && neighbor_pos->col == end_pos[7])){
                maze_to_visit[end_pos[0]][end_pos[1]] = 1;
                maze_to_visit[end_pos[2]][end_pos[3]] = 1;
                maze_to_visit[end_pos[4]][end_pos[5]] = 1;
                maze_to_visit[end_pos[6]][end_pos[7]] = 1;
            }

            else{
                maze_to_visit[neighbor_pos->row][neighbor_pos->col] = 1;              
            }
            
            node_to_visit = maze_visited(maze_to_visit); //verifica se há nós não visitados em toda a matriz
            stk_visited = push(neighbor_pos, stk_visited);
        }
    }

    printf("\n>>> ALGORITMO DEPTH FIRST SEARCH | LABIRINTO [%d]X[%d] CONCLU%cDO <<<\nPassos executados: %d", maze_size_row, maze_size_col, 214, count);
    maze_display_print(maze_display);

    return maze_display;
}

